// App.js
import React, { useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS } from 'chart.js/auto';

// מיפוי תוויות לטווחי זמן (בדקות)
const PERIOD_OPTIONS = {
  'Last hour': 60,
  'Last day': 1440,
  'Last week': 10080,
};

function App() {
  const [ip, setIp] = useState('172.31.88.161');
  const [timePeriodLabel, setTimePeriodLabel] = useState('Last day');
  const [period, setPeriod] = useState(300);      // מרווח דגימות (בשניות)
  const [cpuData, setCpuData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setCpuData(null);

    const timePeriod = PERIOD_OPTIONS[timePeriodLabel];

    try {
      const res = await axios.get('http://localhost:5000/cpu-usage', {
        params: { ip, timePeriod, interval: period }
      });
      console.log('Response data:', res.data);
      setCpuData(res.data);
    } catch (err) {
      setError('Failed to fetch CPU data: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  // בונים את נתוני הגרף
  const chartData = cpuData && {
    labels: cpuData.timestamps.map(ts =>
      new Date(ts).toLocaleTimeString()
    ),
    datasets: [{
      label: 'CPU Usage (%)',
      data: cpuData.usageData,
      fill: true,
      backgroundColor: 'rgba(255, 99, 132, 0.2)',
      borderColor: 'rgba(255, 99, 132, 1)',
      tension: 0.4,
    }]
  };

  // הגדרת אופציות להצגת תוויות צירים
  const options = {
    responsive: true,
    scales: {
      x: {
        title: {
          display: true,
          text: 'Time',
        },
      },
      y: {
        title: {
          display: true,
          text: 'CPU Usage (%)',
        },
        beginAtZero: true,
      },
    },
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'Arial, sans-serif' }}>
      <h1>CPU Usage over Time</h1>

      <form onSubmit={handleSubmit} style={{ marginBottom: '1.5rem' }}>
        <label style={{ marginRight: '1rem' }}>
          Time period:
          <select
            value={timePeriodLabel}
            onChange={e => setTimePeriodLabel(e.target.value)}
            style={{ marginLeft: '0.5rem', padding: '0.25rem' }}
          >
            {Object.keys(PERIOD_OPTIONS).map(label =>
              <option key={label} value={label}>{label}</option>
            )}
          </select>
        </label>

        <label style={{ marginRight: '1rem' }}>
          Interval (sec):
          <input
            type="number"
            value={period}
            onChange={e => setPeriod(Number(e.target.value))}
            style={{
              marginLeft: '0.5rem',
              padding: '0.25rem',
              width: '5rem'
            }}
          />
        </label>

        <label style={{ marginRight: '1rem' }}>
          Instance IP:
          <input
            type="text"
            value={ip}
            onChange={e => setIp(e.target.value)}
            style={{
              marginLeft: '0.5rem',
              padding: '0.25rem',
              border: '1px solid #ccc',
              borderRadius: 4,
              width: '10rem'
            }}
          />
        </label>

        <button type="submit" style={{ padding: '0.5rem 1rem' }}>
          Load Data
        </button>
      </form>

      {loading && <p>Loading…</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {cpuData && (
        <Line data={chartData} options={options} />
      )}
    </div>
  );
}

export default App;
