// App.js
import React, { useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS } from 'chart.js/auto';

const PERIOD_OPTIONS = {
  'Last hour': 60,
  'Last day': 1440,
  'Last week': 10080,
};

const INTERVAL_OPTIONS = {
  '1 min': 60,
  '5 min': 300,
  '15 min': 900,
};

function App() {
  const [ip, setIp] = useState('172.31.88.161');
  const [timePeriodLabel, setTimePeriodLabel] = useState('Last day');
  const [intervalLabel, setIntervalLabel] = useState('5 min');
  const [cpuData, setCpuData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setCpuData(null);

    const timePeriod = PERIOD_OPTIONS[timePeriodLabel];
    const interval = INTERVAL_OPTIONS[intervalLabel];

    try {
      const res = await axios.get('http://localhost:5000/cpu-usage', {
        params: { ip, timePeriod, interval }
      });
      setCpuData(res.data);
    } catch (err) {
      setError('Failed to fetch CPU data: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  const chartData = cpuData && {
    labels: cpuData.timestamps.map(ts => new Date(ts).toLocaleTimeString()),
    datasets: [{
      label: 'CPU Usage (%)',
      data: cpuData.usageData,
      fill: true,
      backgroundColor: 'rgba(255, 99, 132, 0.2)',
      borderColor: 'rgba(255, 99, 132, 1)',
      tension: 0.4,
    }]
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>CPU Usage over Time</h1>
      <form onSubmit={handleSubmit} style={{ marginBottom: 20 }}>
        <label style={{ marginRight: 10 }}>
          Time period:
          <select
            value={timePeriodLabel}
            onChange={e => setTimePeriodLabel(e.target.value)}
            style={{ marginLeft: 5, padding: '4px' }}
          >
            {Object.keys(PERIOD_OPTIONS).map(label =>
              <option key={label} value={label}>{label}</option>
            )}
          </select>
        </label>

        <label style={{ marginRight: 10 }}>
          Period:
          <select
            value={intervalLabel}
            onChange={e => setIntervalLabel(e.target.value)}
            style={{ marginLeft: 5, padding: '4px' }}
          >
            {Object.keys(INTERVAL_OPTIONS).map(label =>
              <option key={label} value={label}>{label}</option>
            )}
          </select>
        </label>

        <label style={{ marginRight: 10 }}>
          Instance IP:
          <input
            type="text"
            value={ip}
            onChange={e => setIp(e.target.value)}
            style={{
              marginLeft: 5,
              padding: '4px',
              border: '1px solid #ccc',
              borderRadius: 4
            }}
          />
        </label>

        <button type="submit">Load Data</button>
      </form>

      {loading && <p>Loadingâ€¦</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {cpuData && <Line data={chartData} />}
    </div>
  );
}

export default App;
