import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS } from 'chart.js/auto';

function App() {
  const [cpuData, setCpuData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/cpu-usage')
      .then(response => {
        setCpuData(response.data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  // יצירת גרף פשוט
  const data = {
    labels: cpuData ? cpuData.timestamps : [], // אם יש נתוני תאריכים
    datasets: [
      {
        label: 'CPU Usage',
        data: cpuData ? cpuData.usageData : [], // נתוני השימוש ב-CPU
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1
      }
    ]
  };

  return (
    <div>
      <h1>CPU Usage over Time</h1>
      <Line data={data} />
    </div>
  );
}

export default App;
