import React, { useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS } from 'chart.js/auto';

function App() {
  const [cpuData, setCpuData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // קלטים: IP, תקופה ומרווח דגימות
  const [ip, setIp] = useState('');
  const [timePeriod, setTimePeriod] = useState('');
  const [interval, setInterval] = useState('');

  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    setIsSubmitted(true);

    // יצירת בקשה לשרת Flask (במקום הכתובת שלך תשים את ה-IP שהמשתמש מזין)
    axios.get(`http://${ip}:5000/cpu-usage?timePeriod=${timePeriod}&interval=${interval}`)
      .then(response => {
        setCpuData(response.data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  // יצירת גרף פשוט
  const data = {
    labels: cpuData ? cpuData.timestamps : [], // אם יש נתוני תאריכים
    datasets: [
      {
        label: 'CPU Usage',
        data: cpuData ? cpuData.usageData : [], // נתוני השימוש ב-CPU
        fill: false,
        borderColor: 'rgb(75, 192, 192)',
        tension: 0.1
      }
    ]
  };

  return (
    <div>
      <h1>CPU Usage over Time</h1>

      {/* טופס לקלטים */}
      <form onSubmit={handleSubmit}>

        <div>
          <label>Time Period (minutes):</label>
          <input
            type="text"
            value={timePeriod}
            onChange={(e) => setTimePeriod(e.target.value)}
            placeholder="Enter time period"
            required
          />
        </div>
        <div>
          <label>Interval (seconds):</label>
          <input
            type="number"
            value={interval}
            onChange={(e) => setInterval(e.target.value)}
            placeholder="Enter interval"
            required
          />
        </div>
        <div>
          <label>IP Address:</label>
          <input
            type="text"
            value={ip}
            onChange={(e) => setIp(e.target.value)}
            placeholder="Enter server IP"
            required
          />
        </div>
        <button type="submit">Fetch Data</button>
      </form>

      {isSubmitted && cpuData && (
        <div>
          <h2>CPU Usage Chart</h2>
          <Line data={data} />
        </div>
      )}
    </div>
  );
}

export default App;
